import React, {useState, useEffect} from 'react'

import './App.css';
import Movie from './Components/Movie';
const TOP_RATED = "https://api.themoviedb.org/3/movie/top_rated?api_key=2fd99db9a55f38b3057e417bf6d2113a&language=en-US&page=1"

function App() {

const [movies, setMovies] = useState([]);
useEffect (() => { 
fetch(TOP_RATED[0])
.then(res => res.json())
.then(data =>{
  console.log(data[0]);
  setMovies(data.results);
}); 

}, []);


  return (
    <div>
      <h1>Top Rated Movies</h1>
    <div className="movie-container">
      {movies.length > 0 && movies.map((movie) => <Movie key={movie.id} {...movie} />   )}
    </div>
    </div>
  );
}

export default App;
