import React, { Component } from 'react';
import './SearchBox.css';


class SearchBox extends Component {
  constructor(props){
super(props)

const movies = [
  {id: "1", title: "Shaolin Soccer", poster: "https://upload.wikimedia.org/wikipedia/en/thumb/3/3e/ShaolinSoccerFilmPoster.jpg/220px-ShaolinSoccerFilmPoster.jpg"},
  {id: "2", title: "GOAL 2", poster: "https://images-na.ssl-images-amazon.com/images/I/51D%2BKYxbntL.jpg"},
]
var movieRows = []
movies.forEach((movie) =>{
  console.log(movie.title)


  const movieRow = <div className="movie">
  <div className="movie-header">
    <img src = {movie.poster} alt={movie.title}/>
    <div className="movie-info">
      <h3>
        {movie.title}
      </h3>
    </div>
  </div>
  </div>




  movieRows.push(movieRow)
})
this.state = {rows: movieRows}
  }

  render(){
  return (
    <div>
    <div class="searchBody">

<div class="search__container">
    <p class="search__title">
      MoviesXd
    </p>
    <input class="search__input" type="text" placeholder="Search"/>
</div>


</div>
<ul class="list">{this.state.rows}</ul>

<div>

</div>



</div>
  )
}
}

export default SearchBox;
