import React, {useState, useEffect} from 'react'

import './App.css';
import Movie from './Components/Movie';
import Horror from './Components/Horror';
const TOP_RATED = "https://api.themoviedb.org/3/movie/top_rated?api_key=2fd99db9a55f38b3057e417bf6d2113a&language=en-US&page=2"
const HORROR = "https://api.themoviedb.org/3/discover/movie?api_key=2fd99db9a55f38b3057e417bf6d2113a&with_genres=27"
function App() {

const [movies, setMovies] = useState([]);
useEffect (() => { 
fetch(TOP_RATED)
.then(res => res.json())
.then(data =>{
  console.log(data);
  setMovies(data.results);
}); 

}, []);

const [horror, setHorrorMovies] = useState([]);
useEffect (() => { 
fetch(HORROR)
.then(res => res.json())
.then(data =>{
  console.log(data);
  setHorrorMovies(data.results);
}); 

}, []);

  return (
    <>
    <div class="container">
  <input placeholder='Search...' class='js-search' type="text"/>
  <i class="fa fa-search"></i>
</div>
      <h1>Top Rated Movies</h1>
    <div className="movie-container">
      {movies.length > 0 && movies.map((movie) => <Movie key={movie.id} {...movie} />   )}
    </div>
    <h1>Horror Movies</h1>
    <div className="movie-container">
      {horror.length > 0 && horror.map((movie) => <Horror key={movie.id} {...movie} />   )}
    </div>
    </>
  );
}

export default App;
