import './App.css';
import Movie from './Components/Movie';
const TOP_RATED = "https://api.themoviedb.org/3/movie/top_rated?api_key=2fd99db9a55f38b3057e417bf6d2113a&language=en-US&page=1"

function App() {

const [movies, setMovies] = useState([]);
useEffect (() => { 
fetch(TOP_RATED)
.then(res => res.json())
.then(data =>{
  setMovies(data);
}); 

}, []);


  return (
    <div className="App">
      {movies.length > 0 && movies.map((movie) => <Movie />   )}
    </div>
  );
}

export default App;
