import React from 'react';


export default class SearchBox extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        suggestions: [],
        text:''
      }
    }
    
    onTextChange = (e) => {
      const {iteams} = this.props;
      let suggestions = [];
      const value = e.target.value;
      if (value.length > 0) {
        const regex = new RegExp(`^${value}`, `i`);
        suggestions = iteams.sort().filter(v => regex.test(v));
      }
   
   
      this.setState(() => ({
        suggestions,
        text:value
      }));
    }
    
    
    suggestionSelected=(value)=>{
      this.setState(()=>({
        text:value,
        suggestions:[]
      }))
    }
    
    renderSuggestions = () => {
      const { suggestions } = this.state;
      console.log("suggestions :",suggestions);
      if (suggestions.length === 0) {
        return null;
      }
      return (
        <ul class="dList">
    {suggestions.map(city =><a href={`/movies/${city}`}><li key={city} onClick={(e)=>this.suggestionSelected(city)}>{city}</li></a>)}
        </ul>
      )
    }

render() {
    const {text}=this.state
    return (
        <div>
  <div className="TypeAheadDropDown">
  <li class="search-icon">
            <input onChange={this.onTextChange} value={text} type="search" placeholder="Search"/>
            <label class="icon">
              <span class="fas fa-search"></span>
            </label>
          </li>
          </div>
  <div class="fun">
            {this.renderSuggestions()}</div>
  </div>
    );
}

}
