import React, { useEffect, useState } from 'react'

const APIKEY = "?api_key=2fd99db9a55f38b3057e417bf6d2113a&language=en-US";
const API = "https://api.themoviedb.org/3/movie/";



function MoviePage(props) {
    const [movies, setMovies] = useState([]);
    useEffect (() => { 
        fetch(`https://api.themoviedb.org/3/movie/${this.props}?api_key=2fd99db9a55f38b3057e417bf6d2113a&language=en-US`)
        .then(res => res.json())
        .then(data =>{
          console.log(data);
          setMovies(data.results);
        }); 
        
      }, []);
    return (
        <div>
            {movies.title}
        </div>
    )
}

export default MoviePage
